mapper.update:
UPDATE Accounts SET Password = #{password, jdbcType=VARCHAR}, FullName = #{fullName, jdbcType=VARCHAR}, Address = #{address, jdbcType=VARCHAR}, Phone = #{phone, jdbcType=VARCHAR},
Image = #{image, javaType=byte[], jdbcType=BLOB, typeHandler=org.apache.ibatis.type.BlobTypeHandler}
WHERE Email = #{email, jdbcType=VARCHAR}
